options.customCss:
/* This section adds the bold, neon effect to your title */
.n8n-form-html h1 {
    color: #ffffff;
    font-weight: bold;
    text-align: center;
    margin-bottom: 20px;
    /* Neon glow effect updated to match your lime green brand */
    text-shadow:
        0 0 5px #fff,
        0 0 10px #fff,
        0 0 15px #9ACD32,
        0 0 20px #9ACD32,
        0 0 25px #9ACD32,
        0 0 30px #9ACD32;
}

:root {
	/* Font and size variables - unchanged */
	--font-family: 'Open Sans', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;
	--font-size-body: 12px;
	--font-size-label: 14px;
	--font-size-test-notice: 12px;
	--font-size-input: 14px;
	--font-size-header: 20px;
	--font-size-paragraph: 14px;
	--font-size-link: 12px;
	--font-size-error: 12px;
	--font-size-html-h1: 28px;
	--font-size-html-h2: 20px;
	--font-size-html-h3: 16px;
	--font-size-html-h4: 14px;
	--font-size-html-h5: 12px;
	--font-size-html-h6: 10px;
	--font-size-subheader: 14px;

	/* Colors - Updated to match your lime green brand */
	--color-background: #000000; /* Pure black like your logo background */
	--color-card-bg: #1A1A1A; /* Slightly lighter dark for cards */
	--color-card-border: #9ACD32; /* Your brand lime green for borders */
	--color-card-shadow: rgba(154, 205, 50, 0.2); /* Lime green glow shadow */
	--color-header: #F0F0F0; /* Light header text */
	--color-label: #E0E0E0; /* Light text for labels */
	--color-input-border: #333333; /* Dark grey for input borders */
	--color-input-text: #000000; /* Black text inside inputs */
	--color-focus-border: #9ACD32; /* Lime green for focus */
	--color-submit-btn-bg: #9ACD32; /* Lime green submit button */
	--color-submit-btn-text: #000000; /* Black text on button */
	--color-link: #ADFF2F; /* Lighter lime for links */
	--color-html-text: #F0F0F0; /* General text color */
	--color-html-link: #9ACD32; /* Lime green for HTML links */
	--color-header-subtext: #A0A0A0; /* Grey for sub-headers */
	--color-required: #9ACD32; /* Lime green for required indicators */
	--color-error: #FF4444; /* Bright red for errors */
	--color-clear-button-bg: #7e8186;

	/* Test notice colors - themed to match */
	--color-test-notice-text: #9ACD32;
	--color-test-notice-bg: #1A1A1A;
	--color-test-notice-border: #9ACD32;

	/* Layout variables - unchanged */
	--border-radius-card: 8px;
	--border-radius-input: 6px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 8px;
	--padding-container-top: 24px;
	--padding-card: 24px;
	--padding-test-notice-vertical: 12px;
	--padding-test-notice-horizontal: 24px;
	--margin-bottom-card: 16px;
	--padding-form-input: 12px;
	--card-padding: 24px;
	--card-margin-bottom: 16px;
	--container-width: 448px;
	--submit-btn-height: 48px;
	--checkbox-size: 18px;
	--box-shadow-card: 0px 4px 16px 0px var(--color-card-shadow);
	--opacity-placeholder: 0.5;
}

/* --- Black Placeholder Text --- */
.n8n-form-field-input::placeholder,
textarea::placeholder {
  color: #000000 !important;
  opacity: 1 !important;
}
.n8n-form-field-input::-moz-placeholder,
textarea::-moz-placeholder {
  color: #000000 !important;
  opacity: 1 !important;
}
.n8n-form-field-input:-ms-input-placeholder,
textarea:-ms-input-placeholder {
  color: #000000 !important;
  opacity: 1 !important;
}
.n8n-form-field-input::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {
  color: #000000 !important;
  opacity: 1 !important;
}

/* Optional: Add hover effects for interactive elements */
.n8n-form-submit:hover {
    background-color: #ADFF2F !important;
    box-shadow: 0 0 20px rgba(154, 205, 50, 0.5);
    transition: all 0.3s ease;
}

/* Optional: Style input focus with brand glow */
.n8n-form-field-input:focus {
    border-color: var(--color-focus-border) !important;
    box-shadow: 0 0 10px rgba(154, 205, 50, 0.3);
    outline: none;
}

