// Get the input data
const inputItems = items;
const outputItems = [];

for (let i = 0; i < inputItems.length; i++) {
  try {
    const item = inputItems[i].json;

    // Get the main content
    const content = item?.data?.metadata?.ogTitle || "";

    // Extract company name
    let companyName = "Unknown";
    const companyNameMatch = content.match(/# ([A-Za-z0-9]+)Reviews(\d+)/);
    if (companyNameMatch) {
      companyName = companyNameMatch[1];
    }

    // Extract trust score
    let trustScore = "N/A";
    const trustScoreMatch = content.match(/TrustScore (\d+\.?\d*) out of 5/);
    if (trustScoreMatch) {
      trustScore = trustScoreMatch[1];
    }

    // Extract total reviews
    let totalReviews = "0";
    const totalReviewsMatch = content.match(/Reviews(\d+)/);
    if (totalReviewsMatch) {
      totalReviews = totalReviewsMatch[1];
    }

    // Extract reviews using a different approach
    const reviews = [];

    // Find all review blocks
    // Look for patterns like "![Rated X out of 5 stars]"
    const ratingMatches = [...content.matchAll(/!\[Rated (\d) out of 5 stars\]/g)];

    for (let j = 0; j < ratingMatches.length; j++) {
      const ratingMatch = ratingMatches[j];
      const rating = ratingMatch[1]; // This contains the actual rating (1-5)

      // Get the position where this rating appears in the content
      const startPos = ratingMatch.index;

      // Define the end position (either the next rating or the end of content)
      const endPos = (j < ratingMatches.length - 1) ? ratingMatches[j + 1].index : content.length;

      // Extract this review's block of text
      const reviewBlock = content.substring(startPos, endPos);

      // Extract review title
      let title = "";
      const titleMatch = reviewBlock.match(/\*\*([^*]+)\*\*/);
      if (titleMatch) {
        title = titleMatch[1].trim();
      } else {
        // Try alternate title formats
        const altTitleMatch = reviewBlock.match(/\n\n\[([^\]]+)\]/);
        if (altTitleMatch) {
          title = altTitleMatch[1].trim();
        }
      }

      // Extract review content - find text after the rating that's not part of metadata
      let reviewContent = "";
      // First look for text between the title and "Date of experience"
      if (reviewBlock.includes("**Date of experience:**")) {
        const textBeforeDate = reviewBlock.split("**Date of experience:**")[0];

        // Extract the main content, skipping the title and other metadata
        const contentMatch = textBeforeDate.match(/\]\(https:\/\/www\.trustpilot\.com\/reviews\/[^)]+\) ([\s\S]+)$/);
        if (contentMatch) {
          reviewContent = contentMatch[1].trim();
        } else {
          // If no URL pattern, try a simpler approach
          const simpleMatch = textBeforeDate.match(/\n\n([^*\n]+(?:\n[^*\n]+)*)\n\n$/);
          if (simpleMatch) {
            reviewContent = simpleMatch[1].trim();
          }
        }
      } else {
        // If there's no date pattern, get content after any metadata
        const contentMatch = reviewBlock.match(/\n\n([^*\n\[]+(?:\n[^*\n\[]+)*)\n*$/);
        if (contentMatch) {
          reviewContent = contentMatch[1].trim();
        }
      }

      // Extract experience date
      let experienceDate = "";
      const expDateMatch = reviewBlock.match(/\*\*Date of experience:\*\* ([^\n]+)/);
      if (expDateMatch) {
        experienceDate = expDateMatch[1].trim();
      }

      // Fallback for review content - if nothing extracted properly
      if (!reviewContent && title) {
        // Just get all text in the block after the title
        const titlePos = reviewBlock.indexOf(title);
        if (titlePos > -1) {
          reviewContent = reviewBlock.substring(titlePos + title.length).replace(/^\*+|\n+|\*+$/g, '').trim();
        }
      }

      // Add the review if we have at least something
      if (title || reviewContent) {
        reviews.push({
          rating: rating,
          title: title || "No title",
          content: reviewContent || "No content",
          experience_date: experienceDate
        });
      }
    }

    // Output the structured data
    outputItems.push({
      json: {
        company: companyName,
        total_reviews: totalReviews,
        trust_score: trustScore,
        reviews: reviews
      }
    });
  } catch (error) {
    // If there's an error, output a default object
    outputItems.push({
      json: {
        company: "Error extracting data",
        total_reviews: "0",
        trust_score: "N/A",
        reviews: [],
        error: error.message
      }
    });
  }
}

return outputItems;

